mapper.select:
SELECT COUNT(*)
FROM transactions t
         JOIN accounts a ON t.account_id = a.account_id
WHERE a.user_id = #{userId}
  AND t.payee_account_number = #{accountNumber}
  AND t.deposit_withdrawal = 'WITHDRAWAL'
  AND t.created_time >= #{fromDate}

SELECT COUNT(*)
FROM transactions t
         JOIN accounts a ON t.account_id = a.account_id
WHERE a.user_id = #{userId}
  AND t.deposit_withdrawal = 'WITHDRAWAL'
  AND DATE(t.created_time) = #{transactionDate}

SELECT EXISTS(
    SELECT 1
    FROM favorites f
    WHERE f.user_id = #{userId}
      AND f.send_account_number = #{accountNumber}
)
